mapper.select:
SELECT tb_board.id , tb_board.title , tb_board.content , tb_board.deleted , tb_board.created_at AS createdAt , tb_board.modified_at AS modifiedAt , tb_board.tb_user_id AS tbUserId , tb_user.nick AS author FROM tb_board, tb_user WHERE tb_board.id is not null AND tb_board.tb_user_id = tb_user.id AND tb_board.created_at >= CONCAT(#{beginDate}, ' 00:00:00.000000') AND CONCAT(#{endDate}, ' 23:59:59.999999') >= tb_board.created_at AND tb_board.title LIKE CONCAT('%', #{title}, '%') AND tb_board.deleted = #{deleted}
AND tb_board.created_at > STR_TO_DATE(#{searchDate}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tb_board.created_at ASC AND STR_TO_DATE(#{searchDate}, '%Y-%m-%d %H:%i:%s.%f') > tb_board.created_at ORDER BY tb_board.created_at DESC
ORDER BY tb_board.created_at ASC LIMIT 0, #{requestBoardSize}
